var addDisplayCallback = function(pixel, pins, w, h) {
	for (var y = 1; y < h - 1; y++) {
		for (var x = 1; x < w - 1; x++) {
			var px = pixel.x + x;
			var py = pixel.y + y;
			if (!(0 <= px && px < width && 0 <= py && py < height)) {continue;}

			deletePixel(px, py);
			createPixel("displayPixel", px, py);
			pixelMap[px][py].color = "rgb(16, 24, 32)";
		}
	}
}

